    const HistoryManager = {
        stack: [],
        maxSize: 50, // å±¥æ­´ã®æœ€å¤§ä¿æŒæ•°

        push: function(state) {
            if (!state) return;
            
            try {
                // ã‚ªãƒ–ã‚¸ã‚§ã‚¯ãƒˆã‚’JSONæ–‡å­—åˆ—åŒ–
                const jsonString = JSON.stringify(state);
                
                // ç›´å‰ã®çŠ¶æ…‹ã¨æ¯”è¼ƒã—ã¦å¤‰æ›´ãŒãªã‘ã‚Œã°ä¿å­˜ã—ãªã„
                if (this.stack.length > 0) {
                    const lastItem = this.stack[this.stack.length - 1];
                    let lastJsonString;
                    
                    if (lastItem.compressed && typeof pako !== 'undefined') {
                        lastJsonString = pako.inflate(lastItem.data, { to: 'string' });
                    } else {
                        lastJsonString = lastItem.data;
                    }
                    
                    if (lastJsonString === jsonString) {
                        // çŠ¶æ…‹ã«å¤‰æ›´ãŒãªã„ãŸã‚ä¿å­˜ã—ãªã„
                        return;
                    }
                }

                // pakoãŒåˆ©ç”¨å¯èƒ½ãªã‚‰åœ§ç¸®ã—ã¦ä¿å­˜ã€ãªã‘ã‚Œã°æ–‡å­—åˆ—ã®ã¾ã¾ä¿å­˜
                if (typeof pako !== 'undefined') {
                    const compressed = pako.deflate(jsonString);
                    this.stack.push({ compressed: true, data: compressed });
                } else {
                    this.stack.push({ compressed: false, data: jsonString });
                }

                // ã‚µã‚¤ã‚ºåˆ¶é™ã‚’è¶…ãˆãŸã‚‰å¤ã„å±¥æ­´ã‚’å‰Šé™¤
                if (this.stack.length > this.maxSize) {
                    this.stack.shift();
                }
                
                this.updateUI();
            } catch (e) {
                console.error('å±¥æ­´ä¿å­˜ã‚¨ãƒ©ãƒ¼:', e);
            }
        },

        pop: function() {
            if (this.stack.length === 0) return null;

            try {
                const item = this.stack.pop();
                let jsonString;
                
                if (item.compressed && typeof pako !== 'undefined') {
                    jsonString = pako.inflate(item.data, { to: 'string' });
                } else {
                    jsonString = item.data;
                }
                
                this.updateUI();
                return JSON.parse(jsonString);
            } catch (e) {
                console.error('å±¥æ­´å¾©å…ƒã‚¨ãƒ©ãƒ¼:', e);
                return null;
            }
        },

        clear: function() {
            this.stack = [];
            this.updateUI();
        },

        updateUI: function() {
            const undoBtn = document.getElementById('undo-btn');
            if (undoBtn) {
                const hasHistory = this.stack.length > 0;
                undoBtn.disabled = !hasHistory;
                undoBtn.style.opacity = hasHistory ? '1' : '0.5';
                undoBtn.title = hasHistory ? "å…ƒã«æˆ»ã™ (ã‚·ãƒ§ãƒ¼ãƒˆã‚«ãƒƒãƒˆ: Ctrl+Z)" : "å…ƒã«æˆ»ã™å±¥æ­´ãŒã‚ã‚Šã¾ã›ã‚“";
            }
        }
    };

