                const pos = transform(n.x, n.y); 
                // ç¯€ç‚¹ç•ªå·ã¯é»’ã§è¡¨ç¤ºï¼ˆä¸¸å›²ã„ï¼‰
                const fs2 = fontScale || (window.settings?.fontScales?.model || 1.0);
                const fontSize = 10 * fs2; // 12 -> 10 ã«å¤‰æ›´
                ctx.font = `${fontSize}px Arial`;
                ctx.textAlign = "center";
                ctx.textBaseline = "middle";
                
                const text = (i + 1).toString();
                // ãƒ†ã‚­ã‚¹ãƒˆã‚µã‚¤ã‚ºã«åŸºã¥ã„ã¦åŠå¾„ã‚’è¨ˆç®—ï¼ˆæœ€å°ã‚µã‚¤ã‚ºã‚’ç¢ºä¿ï¼‰
                const metrics = ctx.measureText(text);
                const radius = Math.max(metrics.width / 2 + 3 * fs2, fontSize / 2 + 2 * fs2);
                
                // ç¯€ç‚¹ã‹ã‚‰ã®ã‚ªãƒ•ã‚»ãƒƒãƒˆä½ç½®ï¼ˆå³ä¸Šï¼‰
