            const midX = (p1.x + p2.x) / 2; 
            const midY = (p1.y + p2.y) / 2; 
            const angle = Math.atan2(p2.y - p1.y, p2.x - p1.x); 
            const offsetX = offset * Math.sin(angle); 
            const offsetY = -offset * Math.cos(angle); 
            const labelTargetX = midX + offsetX; 
