    // ãƒãƒƒãƒ—ã‚¢ãƒƒãƒ—ã®ä½ç½®ã‚’å‹•çš„ã«å†èª¿æ•´ã™ã‚‹é–¢æ•°
    function adjustPopupPosition(popup, targetBounds = null) {
        console.log('ðŸ“ adjustPopupPositionå‘¼ã³å‡ºã—:', {
            popup: popup?.id,
            display: popup?.style.display,
            targetBounds: targetBounds
        });
        
        if (!popup || popup.style.display === 'none') {
            console.log('âŒ ãƒãƒƒãƒ—ã‚¢ãƒƒãƒ—ãŒéžè¡¨ç¤ºã¾ãŸã¯å­˜åœ¨ã—ã¾ã›ã‚“');
            return;
        }
        
        // ç¾åœ¨ã®ãƒãƒƒãƒ—ã‚¢ãƒƒãƒ—ã‚µã‚¤ã‚ºã‚’å–å¾—
        const popupRect = popup.getBoundingClientRect();
        const popupWidth = popupRect.width;
        const popupHeight = popupRect.height;
        
        console.log('ðŸ“ ãƒãƒƒãƒ—ã‚¢ãƒƒãƒ—ã‚µã‚¤ã‚º:', {
            width: popupWidth,
            height: popupHeight,
            currentRect: popupRect
        });
        const viewport = getVisibleViewportBoundsInThisWindow();
        const windowWidth = viewport.width;
        const availableHeight = viewport.height;
        
        const minMargin = 10;
        const bottomMargin = 20; // ã‚¿ã‚¹ã‚¯ãƒãƒ¼å¯¾ç­–ã§ã‚ˆã‚Šå¤§ããªãƒžãƒ¼ã‚¸ãƒ³
        
        let left = parseInt(popup.style.left) || 0;
        let top = parseInt(popup.style.top) || 0;
        
        if (targetBounds) {
            // éƒ¨æä½ç½®ã‚’é¿ã‘ã¦å†é…ç½®
            const margin = 20;
            
            // å³å´ã«é…ç½®ã‚’è©¦è¡Œ
            left = targetBounds.right + margin;
            if (left + popupWidth > viewport.right - minMargin) {
                // å³å´ã«åŽã¾ã‚‰ãªã„å ´åˆã¯å·¦å´ã«é…ç½®
                left = targetBounds.left - popupWidth - margin;
                if (left < viewport.left + minMargin) {
                    // å·¦å´ã«ã‚‚åŽã¾ã‚‰ãªã„å ´åˆã¯ä¸Šä¸‹ã«é…ç½®
                    const centerX = viewport.left + viewport.width / 2;
                    left = Math.max(viewport.left + minMargin, Math.min(centerX - popupWidth / 2, viewport.right - popupWidth - minMargin));
                    top = targetBounds.bottom + margin;
                    if (top + popupHeight > viewport.bottom - bottomMargin) {
                        // ä¸‹å´ã«åŽã¾ã‚‰ãªã„å ´åˆã¯ä¸Šå´ã«é…ç½®
                        top = targetBounds.top - popupHeight - margin;
                        if (top < viewport.top + minMargin) {
                            // ã©ã“ã«ã‚‚åŽã¾ã‚‰ãªã„å ´åˆã¯ç”»é¢ä¸­å¤®ï¼ˆå¼·åˆ¶çš„ã«åŽã‚ã‚‹ï¼‰
                            const cx = viewport.left + viewport.width / 2;
                            const cy = viewport.top + viewport.height / 2;
                            left = Math.max(viewport.left + minMargin, cx - popupWidth / 2);
                            top = Math.max(viewport.top + minMargin, cy - popupHeight / 2);
                        }
                    }
                } else {
                    // å·¦å´ã«é…ç½®ã§ãã‚‹å ´åˆã®ç¸¦ä½ç½®
                    top = Math.max(viewport.top + minMargin, Math.min(targetBounds.top, viewport.bottom - popupHeight - bottomMargin));
                }
            } else {
                // å³å´ã«é…ç½®ã§ãã‚‹å ´åˆã®ç¸¦ä½ç½®
                top = Math.max(viewport.top + minMargin, Math.min(targetBounds.top, viewport.bottom - popupHeight - bottomMargin));
            }
        } else {
            // ç”»é¢å¢ƒç•Œãƒã‚§ãƒƒã‚¯ã®ã¿
            // å³ç«¯ãƒã‚§ãƒƒã‚¯
            if (left + popupWidth > viewport.right - minMargin) {
                left = viewport.right - popupWidth - minMargin;
            }
            // å·¦ç«¯ãƒã‚§ãƒƒã‚¯
            if (left < viewport.left + minMargin) {
                left = viewport.left + minMargin;
            }
            // ä¸‹ç«¯ãƒã‚§ãƒƒã‚¯ï¼ˆã‚¿ã‚¹ã‚¯ãƒãƒ¼å¯¾å¿œï¼‰
            if (top + popupHeight > viewport.bottom - bottomMargin) {
                top = viewport.bottom - popupHeight - bottomMargin;
            }
            // ä¸Šç«¯ãƒã‚§ãƒƒã‚¯
            if (top < minMargin) {
                top = viewport.top + minMargin;
            }
        }
        
        // æœ€çµ‚çš„ã«ç”»é¢å†…ã«å¼·åˆ¶çš„ã«åŽã‚ã‚‹
        left = Math.max(viewport.left + minMargin, Math.min(left, viewport.right - popupWidth - minMargin));
        top = Math.max(viewport.top + minMargin, Math.min(top, viewport.bottom - popupHeight - bottomMargin));
        
        console.log('âœ… ãƒãƒƒãƒ—ã‚¢ãƒƒãƒ—æœ€çµ‚ä½ç½®:', {
            left: left,
            top: top,
            windowWidth: windowWidth,
            availableHeight: availableHeight,
            popupDisplay: popup.style.display
        });
        
        // position: fixedã‚’æ˜Žç¤ºçš„ã«è¨­å®š
        popup.style.position = 'fixed';
        popup.style.left = `${left}px`;
        popup.style.top = `${top}px`;
        // z-indexã‚’ä¸‹ã’ãªã„ï¼ˆä»–ã®overlay/tooltipç­‰ã®èƒŒé¢ã«å›žã‚‹ã®ã‚’é˜²æ­¢ï¼‰
        try {
            const computedZ = parseInt((popup.style.zIndex || window.getComputedStyle(popup).zIndex || '0'), 10);
            const safeZ = Number.isFinite(computedZ) ? computedZ : 0;
            popup.style.zIndex = String(Math.max(99999, safeZ));
        } catch (_) {
            popup.style.zIndex = '99999';
        }
        
        console.log('ðŸŽ¯ ãƒãƒƒãƒ—ã‚¢ãƒƒãƒ—ä½ç½®è¨­å®šå®Œäº†:', {
            styleLeft: popup.style.left,
            styleTop: popup.style.top,
            styleDisplay: popup.style.display,
            stylePosition: popup.style.position,
            styleZIndex: popup.style.zIndex,
            boundingRect: popup.getBoundingClientRect()
        });
    }
    
    // ãƒãƒƒãƒ—ã‚¢ãƒƒãƒ—ã®ãƒ‰ãƒ©ãƒƒã‚°æ©Ÿèƒ½ã‚’è¿½åŠ ã™ã‚‹é–¢æ•°
    function makePopupDraggable(popup) {
        if (!popup) return;
        
        let isDragging = false;
        let dragOffset = { x: 0, y: 0 };
        
        // ãƒ˜ãƒƒãƒ€ãƒ¼éƒ¨åˆ†ã‚’å–å¾—ï¼ˆh4ã‚¿ã‚°å„ªå…ˆã€‚ç„¡ã„å ´åˆã¯å…ˆé ­è¦ç´ ï¼‰
        const header = popup.querySelector('h4') || popup.firstElementChild || popup;
        if (!header) return;
        
        // ãƒ˜ãƒƒãƒ€ãƒ¼ã«ãƒ‰ãƒ©ãƒƒã‚°å¯èƒ½ã§ã‚ã‚‹ã“ã¨ã‚’ç¤ºã™ã‚¹ã‚¿ã‚¤ãƒ«ã‚’é©ç”¨
        header.style.cursor = 'move';
        header.style.userSelect = 'none';
        
        function shouldIgnoreDragStart(e) {
            try {
                const t = e && e.target ? e.target : null;
                if (!t || !t.closest) return false;
                // å…¥åŠ›æ“ä½œãƒ»ãƒœã‚¿ãƒ³æ“ä½œã¯ãƒ‰ãƒ©ãƒƒã‚°é–‹å§‹ã—ãªã„
                if (t.closest('input, textarea, select, button, label, a')) return true;
            } catch (_) {}
            return false;
        }

        function startDrag(e) {
            if (e && e.button !== undefined && e.button !== 0) return; // å·¦ã‚¯ãƒªãƒƒã‚¯ã®ã¿
            if (shouldIgnoreDragStart(e)) return;

            isDragging = true;
            const popupRect = popup.getBoundingClientRect();
            dragOffset.x = e.clientX - popupRect.left;
            dragOffset.y = e.clientY - popupRect.top;

            // left/top ãŒæœªè¨­å®šã®ã¨ãã‚‚ãƒ‰ãƒ©ãƒƒã‚°ã§ãã‚‹ã‚ˆã†ã«ç¾åœ¨ä½ç½®ã‚’å›ºå®šå€¤åŒ–
            if (!popup.style.left) popup.style.left = `${popupRect.left}px`;
            if (!popup.style.top) popup.style.top = `${popupRect.top}px`;
            popup.style.position = 'fixed';
            
            // ãƒãƒƒãƒ—ã‚¢ãƒƒãƒ—ã‚’æœ€å‰é¢ã«ç§»å‹•ã¨ãƒ‰ãƒ©ãƒƒã‚°ã‚¹ã‚¿ã‚¤ãƒ«é©ç”¨ï¼ˆå…ƒã®z-indexã‚’ä¿æŒï¼‰
            const prevZ = popup.style.zIndex || window.getComputedStyle(popup).zIndex || '';
            popup.dataset.prevZIndex = prevZ;
            popup.style.zIndex = '300000';
            popup.classList.add('popup-dragging');
            
            document.addEventListener('mousemove', doDrag);
            document.addEventListener('mouseup', stopDrag);
            e.preventDefault();
        }
        
        function doDrag(e) {
            if (!isDragging) return;

            const viewport = getVisibleViewportBoundsInThisWindow();
            const popupRect = popup.getBoundingClientRect();
            const minMargin = 5;
            const bottomMargin = 20;
            
            // æ–°ã—ã„ä½ç½®ã‚’è¨ˆç®—
            let newLeft = e.clientX - dragOffset.x;
            let newTop = e.clientY - dragOffset.y;
            
            // ç”»é¢å¢ƒç•Œå†…ã«åˆ¶é™
            newLeft = Math.max(viewport.left + minMargin, Math.min(newLeft, viewport.right - popupRect.width - minMargin));
            newTop = Math.max(viewport.top + minMargin, Math.min(newTop, viewport.bottom - popupRect.height - bottomMargin));
            
            popup.style.left = `${newLeft}px`;
            popup.style.top = `${newTop}px`;
        }
        
        function stopDrag() {
            if (isDragging) {
                isDragging = false;
                // z-indexã‚’å…ƒã«æˆ»ã—ã¦ãƒ‰ãƒ©ãƒƒã‚°ã‚¹ã‚¿ã‚¤ãƒ«ã‚’å‰Šé™¤
                try {
                    if (popup.dataset && popup.dataset.prevZIndex !== undefined) {
                        popup.style.zIndex = popup.dataset.prevZIndex;
                        delete popup.dataset.prevZIndex;
                    }
                } catch (_) {}
                popup.classList.remove('popup-dragging');
                document.removeEventListener('mousemove', doDrag);
                document.removeEventListener('mouseup', stopDrag);
            }
        }
        
        header.addEventListener('mousedown', startDrag);
        
        // ã‚¿ãƒƒãƒãƒ‡ãƒã‚¤ã‚¹å¯¾å¿œ
        function startTouchDrag(e) {
            if (e.touches.length === 1) {
                const touch = e.touches[0];
                const mouseEvent = new MouseEvent('mousedown', {
                    clientX: touch.clientX,
                    clientY: touch.clientY
                });
                startDrag(mouseEvent);
            }
        }
        
        function handleTouchMove(e) {
            if (isDragging && e.touches.length === 1) {
                const touch = e.touches[0];
                const mouseEvent = new MouseEvent('mousemove', {
                    clientX: touch.clientX,
                    clientY: touch.clientY
                });
                doDrag(mouseEvent);
                e.preventDefault();
            }
        }
        
        function handleTouchEnd(e) {
            if (isDragging) {
                stopDrag();
                e.preventDefault();
            }
        }
        
        header.addEventListener('touchstart', startTouchDrag, { passive: false });
        document.addEventListener('touchmove', handleTouchMove, { passive: false });
        document.addEventListener('touchend', handleTouchEnd);
    }
    
    // å…¨ã¦ã®ãƒãƒƒãƒ—ã‚¢ãƒƒãƒ—ã«ãƒ‰ãƒ©ãƒƒã‚°æ©Ÿèƒ½ã‚’é©ç”¨
