        const labelOptions = calculateLabelOptions(D, labelScaleMultiplier);
        const margin = calculateDiagramMargin(D, labelOptions.fontSize);
        const viewBox = `${-D/2 - margin} ${-D/2 - margin} ${D + margin*2} ${D + margin*2}`;
